function countdownToBirthday(){const e=(new Date).getTime(),t=new Date("12.13.2025 24:00:00").getTime()-e;t<0&&countdownToBirthdayCompleted();const n=Math.floor(t/864e5),o=Math.floor(t%864e5/36e5),d=Math.floor(t%36e5/6e4),r=Math.floor(t%6e4/1e3);document.getElementById("days").innerHTML=n,document.getElementById("hours").innerHTML=o,document.getElementById("minutes").innerHTML=d,document.getElementById("seconds").innerHTML=r}function countdownToBirthdayCompleted(){clearInterval(countdownToBirthday),document.getElementById("birthday-countdown").style.display="none"}countdownToBirthday(),setInterval(countdownToBirthday,1e3);